<?php
defined('WYSIJA') or die('Restricted access'); class WYSIJA_help_install extends WYSIJA_object{ function WYSIJA_help_install(){ require_once(ABSPATH . 'wp-admin'.DS.'includes'.DS.'upgrade.php'); } function install(){ $values=array(); $this->createTables(); $this->recordDefaultUserField(); $this->defaultSettings($values); $this->defaultList($values); $this->defaultCampaign($values); $helpImport=&WYSIJA::get("import","helper"); $values['importwp_list_id']=$helpImport->importWP(); $this->createPage($values); $this->createWYSIJAdir($values); $this->moveThemes(); $modelConf=&WYSIJA::get("config","model"); $mailModel=&WYSIJA::get("email","model"); $mailModel->blockMe=true; $values["confirm_email_id"]=$mailModel->insert( array("type"=>"0", "from_email"=>$values["from_email"], "from_name"=>$values["from_name"], "replyto_email"=>$values["from_email"], "replyto_name"=>$values["from_name"], "subject"=>$modelConf->getValue("confirm_email_title"), "body"=>$modelConf->getValue("confirm_email_body"), "status"=>"1")); $values['installed']=true; $values['installed_time']=mktime(); $modelConf->save($values); $this->testNLplugins(); $this->wp_notice(str_replace(array('[link]','[/link]'),array('<a href="admin.php?page=wysija_config">','</a>'),__("Wysija has been installed successfully. Go to the [link]settings page[/link] now, and start blasting emails.",WYSIJA))); return true; } function defaultList(&$values){ $model=&WYSIJA::get("list","model"); $listname=__("My first list",WYSIJA); $defaultListId=$model->insert(array( "name"=>$listname, "description"=>__('The list created automatically on install of the Wysija.',WYSIJA), "is_enabled"=>1)); $values['default_list_id']=$defaultListId; } function defaultCampaign($valuesconfig){ $modelCampaign=&WYSIJA::get("campaign","model"); $campaign_id=$modelCampaign->insert( array( "name"=>__('Example Newsletter',WYSIJA), "description"=>__('Default newsletter created automatically during installation.',WYSIJA), )); $modelEmail=&WYSIJA::get("email","model"); $modelEmail->fieldValid=false; $dataEmail=array( "campaign_id"=>$campaign_id, "subject"=>__('Example Newsletter',WYSIJA), "wj_data" => "", "params"=>"", "wj_styles"=>""); $newparams=unserialize(base64_decode($dataEmail['params'])); $newparams['quickselection']['wp-301']['url']=WYSIJA_EDITOR_IMG."default-newsletter/full/sample-newsletter-01_07.png"; $newparams['quickselection']['wp-301']['thumb_url']=WYSIJA_EDITOR_IMG."default-newsletter/sample-newsletter-01_07-150x150.png"; $newparams['quickselection']['wp-302']['url']=WYSIJA_EDITOR_IMG."default-newsletter/full/sample-newsletter-01_10.png"; $newparams['quickselection']['wp-302']['thumb_url']=WYSIJA_EDITOR_IMG."default-newsletter/sample-newsletter-01_10-150x30.png"; $newparams['quickselection']['wp-303']['url']=WYSIJA_EDITOR_IMG."default-newsletter/full/sample-newsletter-01_14.png"; $newparams['quickselection']['wp-303']['thumb_url']=WYSIJA_EDITOR_IMG."default-newsletter/full/sample-newsletter-01_14.png"; $newparams['quickselection']['wp-304']['url']=WYSIJA_EDITOR_IMG."default-newsletter/full/sample-newsletter-01_16.png"; $newparams['quickselection']['wp-304']['thumb_url']=WYSIJA_EDITOR_IMG."default-newsletter/full/sample-newsletter-01_16.png"; $newparams['quickselection']['wp-305']['url']=WYSIJA_EDITOR_IMG."default-newsletter/full/sample-newsletter-01_21.png"; $newparams['quickselection']['wp-305']['thumb_url']=WYSIJA_EDITOR_IMG."default-newsletter/sample-newsletter-01_21-150x16.png"; $newparams['quickselection']['wp-306']['url']=WYSIJA_EDITOR_IMG."default-newsletter/full/sample-newsletter-01_25.png"; $newparams['quickselection']['wp-306']['thumb_url']=WYSIJA_EDITOR_IMG."default-newsletter/sample-newsletter-01_25-150x150.png"; $newparams['quickselection']['wp-307']['url']=WYSIJA_EDITOR_IMG."default-newsletter/full/white-label-logo.png"; $newparams['quickselection']['wp-307']['thumb_url']=WYSIJA_EDITOR_IMG."default-newsletter/full/white-label-logo.png"; $dataEmail['params']=base64_encode(serialize($newparams)); $newwjdata=unserialize(base64_decode($dataEmail['wj_data'])); $newwjdata["header"]["image"]["src"]=WYSIJA_EDITOR_IMG."default-newsletter/full/white-label-logo.png"; $newwjdata["body"]["block-3"]["image"]["src"]=WYSIJA_EDITOR_IMG."default-newsletter/full/sample-newsletter-01_07.png"; $newwjdata["body"]["block-5"]["image"]["src"]=WYSIJA_EDITOR_IMG."default-newsletter/full/sample-newsletter-01_14.png"; $newwjdata["body"]["block-8"]["image"]["src"]=WYSIJA_EDITOR_IMG."default-newsletter/full/sample-newsletter-01_10.png"; $newwjdata["body"]["block-10"]["image"]["src"]=WYSIJA_EDITOR_IMG."default-newsletter/full/sample-newsletter-01_21.png"; $newwjdata["body"]["block-13"]["image"]["src"]=WYSIJA_EDITOR_IMG."default-newsletter/full/sample-newsletter-01_25.png"; $dataEmail['wj_data']=base64_encode(serialize($newwjdata)); $dataEmail['replyto_name']=$dataEmail['from_name']=$valuesconfig['from_name']; $dataEmail['replyto_email']=$dataEmail['from_email']=$valuesconfig['from_email']; $data['email']['email_id']=$modelEmail->insert($dataEmail); $this->notice("Campaign created."); } function createTables(){ $filename = dirname(__FILE__).DS."install.sql"; $handle = fopen($filename, "r"); $query = fread($handle, filesize($filename)); fclose($handle); $modelObj=&WYSIJA::get("user","model"); $query=str_replace("CREATE TABLE IF NOT EXISTS `","CREATE TABLE IF NOT EXISTS `".$modelObj->getPrefix(),$query); $queries=explode("-- QUERY ---",$query); $results=array(); foreach($queries as $qry){ $results[]=dbDelta($qry); } return true; } function createWYSIJAdir(&$values){ $upload_dir = wp_upload_dir(); $dirname=$upload_dir['basedir'].DS."wysija".DS; $url=$upload_dir['baseurl']."/wysija/"; if(!file_exists($dirname)){ if(!mkdir($dirname, 0755,true)){ return false; } } $values['uploadfolder']=$dirname; $values['uploadurl']=$url; $fileHelp=&WYSIJA::get("file","helper"); $resultdir=$fileHelp->makeDir("themes"); if(!$resultdir) return false; } function moveThemes(){ $fileHelp=&WYSIJA::get("file","helper"); $resultdir=$fileHelp->makeDir("templates"); $upload_dir = wp_upload_dir(); $dirname=str_replace("/",DS,$upload_dir['basedir']).DS."wysija".DS."templates".DS; $defaultthemes=WYSIJA_DIR."themes".DS; if(!file_exists($defaultthemes)) return false; $files = scandir($defaultthemes); foreach($files as $filename){ if(!in_array($filename, array('.','..',".DS_Store","Thumbs.db")) && is_dir($defaultthemes.$filename)){ if(!file_exists($defaultthemes.$filename)) continue; $this->rcopy($defaultthemes.$filename, $dirname.$filename); } } } function rrmdir($dir) { if (is_dir($dir)) { $files = scandir($dir); foreach ($files as $file) if ($file != "." && $file != "..") $this->rrmdir("$dir/$file"); rmdir($dir); } else if (file_exists($dir)) unlink($dir); } function rcopy($src, $dst) { if (file_exists($dst)) $this->rrmdir($dst); if (is_dir($src)) { mkdir($dst); $files = scandir($src); foreach ($files as $file) if ($file != "." && $file != "..") $this->rcopy("$src/$file", "$dst/$file"); } else if (file_exists($src)) copy($src, $dst); } function recordDefaultUserField(){ $modelUF=&WYSIJA::get("user_field","model"); $arrayInsert=array( array("name"=>__("First name",WYSIJA),"column_name"=>"firstname","error_message"=>__("Please enter first name",WYSIJA)), array("name"=>__("Last name",WYSIJA),"column_name"=>"lastname","error_message"=>__("Please enter last name",WYSIJA))); foreach($arrayInsert as $insert){ $modelUF->insert($insert); $modelUF->reset(); } } function defaultSettings(&$values){ $datauser=wp_get_current_user(); $values['replyto_name']=$values['from_name']=$datauser->user_login; $values['emails_notified']=$values['replyto_email']=$values['from_email']=$datauser->user_email; } function createPage(&$values){ $my_post = array( 'post_status' => 'publish', 'post_type' => 'wysijap', 'post_author' => 1, 'post_content' => '[wysija_page]', 'post_title' => __("Subscription confirmation",WYSIJA), 'post_name' => 'subscriptions'); $values['confirm_email_link']=wp_insert_post( $my_post ); flush_rewrite_rules(); } function testNLplugins(){ $importHelp=&WYSIJA::get("import","helper"); $importHelp->testPlugins(); } } 